import React from 'react';
import './App.css';
import Phaser from 'phaser'

var config = {
  type: Phaser.AUTO,
  width: 800,
  height: 600,
  scene: {
      preload: preload,
      create: create,
      update: update
  }
};

var game = new Phaser.Game(config);

function preload (){
  game.load.image('sky', '../phaser3-tutorial-src/assets/sky.png');
    this.load.image('ground', '../phaser3-tutorial-src/assets/platform.png');
    this.load.image('star', '../phaser3-tutorial-src/assets/star.png');
    this.load.image('bomb', '../phaser3-tutorial-src/assets/bomb.png');
    this.load.spritesheet('dude', 
        '../phaser3-tutorial-src/assets/dude.png',
        { frameWidth: 32, frameHeight: 48 }
    );
}

function create (){
  this.sky=game.add.sprite(0, 0, 'sky');
  this.add.image(400, 300, 'star');
}

function update ()
{
}

function App() {
  return (
    <div className="App">
    </div>
  );
}

export default App;
